<script>
export default {
  name: "HeroSection",
  data() {
    return {
      sliderTimer: 0,
      activeslide: 1,
      slidercount: 5,
      sliderinterval: 10000,
    };
  },
  mounted: function () {
    this.startSlider();
  },
  methods: {
    startSlider: function () {
      let self = this;
      this.sliderTimer = setInterval(function () {
        self.nextSlide();
      }, 10000);
    },

    prevSlide: function () {
      if (this.activeslide == 1) {
        this.activeslide = this.slidercount;
      } else {
        this.activeslide = this.activeslide - 1;
      }
    },

    nextSlide: function () {
      if (this.activeslide == this.slidercount) {
        this.activeslide = 1;
      } else {
        this.activeslide = this.activeslide + 1;
      }
    },

    goToSlide: function (slideIndex) {
      this.activeslide = slideIndex;
      clearInterval(this.sliderTimer);
      this.startSlider();
    },

    showForm() {
      // const form = document.getElementById("email-drop");
      // form.classList.toggle("show");

      this.$emit("toggleShowForm");
    },
  },
};
